// All functions

// This function is to load a track every time we load the web
window.onload = function () {
  loadTrack(indexNo);
};

// Functions load the track
// This function has important roles for the audio player
function loadTrack(indexNo) {
  // We use exception handler(try, catch) to avoid track not loaded because of an error
  try {
    clearInterval(timer); // We clear interval every time we load a new track
    resetSlider(); // Reset slider from beginning because we load a new track

    embedder.innerHTML = songs[indexNo].embedUrl;
    track.src = songs[indexNo].path; // Add track source from current index
    title.forEach((element) => {
      element.innerHTML = songs[indexNo].title;
    });
    artist.forEach((element, i) => {
      element.innerHTML = songs[indexNo].artist;
    });
    track.load();
    coverVideo.appendChild(track);

    setTimeout(function () {
      // Set interval for 1 second before executing the function to avoid error
      loadPrevTrack(indexNo); // Load the previous track in the player
      loadNextTrack(indexNo); // Load the next track in the player
    }, 1000);

    timer = setInterval(rangeSlider, 3000); // Set interval for 3 seconds before executing the function to avoid error
  } catch (e) {}
}

// Function to load previous track on list
function loadPrevTrack(indexNo) {
  if (indexNo === 0) {
    // If the track is the first track on the list then the previous track will be the last one
    prevTitle.forEach((element) => {
      element.innerHTML = songs[songs.length - 1].title;
    });
    prevArtist.forEach((element) => {
      element.innerHTML = songs[songs.length - 1].artist;
    });
  } else {
    // Current track is not the first track on the list
    prevTitle.forEach((element) => {
      element.innerHTML = songs[indexNo - 1].title;
    });
    prevArtist.forEach((element) => {
      element.innerHTML = songs[indexNo - 1].artist;
    });
  }
}

// Function to load next track on list
function loadNextTrack(indexNo) {
  if (indexNo === songs.length - 1) {
    // If the track is the last track on the list then the next track will be the first one
    nextTitle.forEach((element) => {
      element.innerHTML = songs[0].title;
    });
    nextArtist.forEach((element) => {
      element.innerHTML = songs[0].artist;
    });
  } else {
    // Current track is not the last track on the list
    nextTitle.forEach((element) => {
      element.innerHTML = songs[indexNo + 1].title;
    });
    nextArtist.forEach((element) => {
      element.innerHTML = songs[indexNo + 1].artist;
    });
  }
}

// Function to load song in pop up song list
function loadListTrack(listIndex) {
  const songElements = [
    document.getElementById('first-song'),
    document.getElementById('second-song'),
    document.getElementById('third-song'),
    document.getElementById('fourth-song'),
    document.getElementById('fifth-song'),
  ];

  for (let i = 0; i < 5; i++) {
    const songIndex = (listIndex + i) % songs.length;
    songElements[i].innerHTML =
      songIndex +
      1 +
      '. ' +
      songs[songIndex].title +
      ' - ' +
      songs[songIndex].artist;
  }
}

// Function to next visible track in pop up song list
function nextList() {
  listIndex += 1; // We just need to add the first song index
  if (listIndex === songs.length) {
    // If we reach the last song of the song list, then we reset to the first one
    listIndex = 0;
  }
  loadListTrack(listIndex);
}

// Function to play the track based on the chosen song in the pop up list
function playTrack(track) {
  const songIndex = (listIndex + track - 1) % songs.length;
  indexNo = songIndex;
  loadTrack(indexNo);
  playSong();
}

// Mute sound function
function muteSound() {
  if (recentVolume.value != 0) {
    // Check if current value != 0 for save last volume
    lastVolume = recentVolume.value;
  }
  // Set all value to 0
  track.volume = 0;
  recentVolume.value = 0;
  volumeShow.innerHTML = 0;
  // Change icon to volume up
  document.getElementById('volume_icon').classList.add('fa-volume-off');
  document.getElementById('volume_icon').classList.remove('fa-volume-up');
}

// Unmute sound function
function unmuteSound() {
  // Set all value to the value before (lastVolume)
  track.volume = lastVolume / 100;
  recentVolume.value = lastVolume;
  volumeShow.innerHTML = lastVolume;
  // Change icon to volume off
  document.getElementById('volume_icon').classList.add('fa-volume-up');
  document.getElementById('volume_icon').classList.remove('fa-volume-off');
}

