function openNav() {
  nav.style.width = '250px'; // Set width of the pop up to 250px
  loadListTrack(listIndex);
}
// Function to close the pop up
function closeNav() {
  nav.style.width = '0'; // Set width of the pop up to 0 to hide the pop up
}
